  CONSTRAINT `FK_down_txt_1` FOREIGN KEY (`content_id`) REFERENCES `down` (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=gbk COMMENT='内容的内容';

