t = <span class=\"keyword\"><strong><font color=\"#7f0055\">new</font></strong></span><span> Rectangle(</span><span class=\"number\">0</span><span>, </span><span class=\"number\">0</span><span>, (</span><span class=\"keyword\"><strong><font color=\"#7f0055\">int</font></strong></span><span>) d.getWidth(), (</span><span class=\"keyword\"><strong><font color=\"#7f0055\">int</font></strong></span><span>) d </span></span></li>\r\n		<li>\r\n			<span>.getHeight()); </span></li>\r\n		<li>\r\n			<span><span class=\"keyword\"><strong><font color=\"#7f0055\">byte</font></strong></span><span>[] data = </span><span class=\"keyword\"><strong><font color=\"#7f0055\">null</font></strong></span><span>; </span></span></li>\r\n		<li>\r\n			<span><span class=\"keyword\"><strong><font color=\"#7f0055\">for</font></strong></span><span> (</span><span class=\"keyword\"><strong><font color=\"#7f0055\">int</font></strong></span><span> i = </span><span class=\"number\">0</span><span>; i &lt; </span><span class=\"number\">1000</span><span>; i++) { </span></span></li>\r\n		<li>\r\n			<span>BufferedImage image = rb.createScreenCapture(rt); </span></li>\r\n		<li>\r\n			<span>data = bufferedImageTobytes(image); </span></li>\r\n		<li>\r\n			<span>System.out.println(i + <span class=\"string\"><font color=\"#0000ff\">&quot;--------图片---------&gt;&quot;</font></span><span> + data.length); </span></span></li>\r\n		<li>\r\n			<span>} </span></li>\r\n		<li>\r\n			<span>} </span></li>\r\n		<li>\r\n			&nbsp;</li>\r\n		<li>\r\n			<span><span class=\"keyword\"><strong><font color=\"#7f0055\">private</font></strong></span><span> </span><span class=\"keyword\"><strong><font color=\"#7f0055\">byte</font></strong></span><span>[] bufferedImageTobytes(BufferedImage image) { </span></span></li>\r\n		<li>\r\n			<span>BufferedImage bImage = <span class=\"keyword\"><strong><font color=\"#7f0055\">new</font></strong></span><span> BufferedImage(image.getWidth(</span><span class=\"keyword\"><strong><font color=\"#7f0055\">null</font></strong></span><span>), image </span></span></li>\r\n		<li>\r\n			<span>.getHeight(<span class=\"keyword\"><strong><font color=\"#7f0055\">null</font></strong></span><span>), BufferedImage.TYPE_INT_ARGB); </span></span></li>\r\n		<li>\r\n			<span>Graphics bg = bImage.getGraphics(); </span></li>\r\n		<li>\r\n			<span>bg.drawImage(image, <span class=\"number\">0</span><span>, </span><span class=\"number\">0</span><span>, </span><span class=\"keyword\"><strong><font color=\"#7f0055\">null</font></strong></span><span>); </span></span></li>\r\n		<li>\r\n			<span>bg.dispose(); </span></li>\r\n		<li>\r\n			<span>ByteArrayOutputStream out = <span class=\"keyword\"><strong><font color=\"#7f0055\">new</font></strong></span><span> ByteArrayOutputStream(); </span></span></li>\r\n		<li>\r\n			<span><span class=\"keyword\"><strong><font color=\"#7f0055\">try</font></strong></span><span> { </span></span></li>\r\n		<li>\r\n			<span>ImageIO.write(bImage, <span class=\"string\"><font color=\"#0000ff\">&quot;jpeg&quot;</font></span><span>, out); </span></span></li>\r\n		<li>\r\n			<span>} <span class=\"keyword\"><strong><font color=\"#7f0055\">catch</font></strong></span><span> (IOException e) { </span></span></li>\r\n		<li>\r\n			<span>e.printStackTrace(); </span></li>\r\n		<li>\r\n			<span>} </span></li>\r\n		<li>\r\n			<span><span class=\"keyword\"><strong><font color=\"#7f0055\">return</font></strong></span><span> out.toByteArray(); </span></span></li>\r\n		<li>\r\n			<span>} </span></li>\r\n		<li>\r\n			&nbsp;</li>\r\n		<li>\r\n			<span><span class=\"keyword\"><strong><font color=\"#7f0055\">public</font></strong></span><span> </span><span class=\"keyword\"><strong><font color=\"#7f0055\">static</font></strong></span><span> </span><span class=\"keyword\"><strong><font color=\"#7f0055\">void</font></strong></span><span> main(String args[]) </span><span class=\"keyword\"><strong><font color=\"#7f0055\">throws</font></strong></span><span> Exception { </span></span></l