 (87,20,NULL,1,'MYSQL基础_02（VIP）','MYSQL 建站教程','管理员','原创',NULL,'删除数据库：\r\n    drop database dbname;\r\n创建表：\r\n    create table tablename(column_name1 column_type1 constraints, column_name2\r\n column_type2 constraints,column_n.. )','2013-07-16 16:22:02',NULL,0,NULL,NULL,NULL,NULL,'course_content.jsp',0,10,0),
